$(".link").hover(
  function () {
    $(this).addClass("link--hover");
  }, function () {
    $(this).removeClass("link--hover");
  }
);

$(".card").hover(
  function () {
    $(this).addClass("card--hover");
  }, function () {
    $(this).removeClass("card--hover");
  }
);

$(".up-btn").hover(
  function () {
    $(this).addClass("up-btn--hover");
  }, function () {
    $(this).removeClass("up-btn--hover");
  }
);